import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  operation: '',
  result: '',
  number: '',
};

export const calculateSlice = createSlice({
  name: 'calculate',
  initialState,
  reducers: {
    setResult(state, action) {
      if (state.result === '0' && action.payload === '0') {
        return state;
      }
      if (state.result.includes('.') && action.payload === '.') {
        return state;
      }
      state.result += action.payload;
    },
    setClear(state, action) {
      state.result = '';
    },
    setOperation(state, action) {
      state.operation = action.payload.digit;
      const plus = state.operation === '+';
      if (plus) {
        state.number = state.number + state.result;
        state.operation = '';
      }
      state.number = state.result;
      state.result = '';
    },
    setDel(state, action) {
      state.result = '';
      state.number = '';
    },
  },
});

export const { setResult, setClear, setOperation, setDel } = calculateSlice.actions;

export default calculateSlice.reducer;
