import { useSelector, useDispatch } from 'react-redux';
import DigitButton from './DigitButton';
import './styles.css';

function App() {
  const dispatch = useDispatch();
  const [{ currentOperand, previousOperand, operation }] = useSelector((state) => state.calculate);

  return (
    <div className="calculator-grid">
      <div className="output">
        <div className="previous-operand">
          {previousOperand} {operation}
        </div>
        <div className="current-operand">{currentOperand}</div>
      </div>
      <button className="span-two">AC</button>
      <button>DEL</button>
      <button>รท</button>
      <DigitButton digit={1} />
      <DigitButton digit={2} />
      <DigitButton digit={3} />
      <button>*</button>
      <DigitButton digit={4} />
      <DigitButton digit={5} />
      <DigitButton digit={6} />
      <button>+</button>
      <DigitButton digit={7} />
      <DigitButton digit={8} />
      <DigitButton digit={9} />
      <button>-</button>
      <button>.</button>
      <DigitButton digit={0} />
      <button className="span-two">=</button>
    </div>
  );
}

export default App;
