import { useSelector, useDispatch } from 'react-redux';
import './styles.css';

const ACTIONS = {
  ADD_DIGIT: 'add-digit',
  CHOOS_OPERATION: 'choose-operation',
  CLEAR: 'clear',
  DELETE_DIGIT: 'delete-digit',
  EVALUATE: 'evaluate',
};

