import { useSelector, useDispatch } from 'react-redux';
import React from 'react';
import './styles.css';

function App() {
  const [result, setResult] = React.useState('');

  function handleClick(event) {
    setResult(result.concat(event.digit));
  }

  function handleClear() {
    setResult('');
  }

  function handleDelete() {
    setResult(result.slice(0, -1));
  }

  function handleCalculate() {
    try {
      setResult(eval(result).toString());
    } catch (error) {
      setResult('Error');
    }
  }
  return (
    <div className="calculator-grid">
      <div className="output">
        <div className="current-operand">{result}</div>
      </div>
      <button className="span-two" onClick={handleClear}>
        AC
      </button>
      <button>DEL</button>
      <button>รท</button>
      <button digit="1" onClick={handleClick}>
        1
      </button>
      <button digit="2" onClick={handleClick}>
        2
      </button>
      <button digit="3" onClick={handleClick}>
        3
      </button>
      <button digit="*">*</button>
      <button digit="4" onClick={handleClick}>
        4
      </button>
      <button digit="5" onClick={handleClick}>
        5
      </button>
      <button digit="6" onClick={handleClick}>
        6
      </button>
      <button digit="+">+</button>
      <button digit="7" onClick={handleClick}>
        7
      </button>
      <button digit="8" onClick={handleClick}>
        8
      </button>
      <button digit="9" onClick={handleClick}>
        9
      </button>
      <button digit="-">-</button>
      <button digit=".">.</button>
      <button digit="0" onClick={handleClick}>
        0
      </button>
      <button className="span-two">=</button>
    </div>
  );
}

export default App;
