import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  number: '',
  result: '',
};

export const calculateSlice = createSlice({
  name: 'calculate',
  initialState,
  reducers: {
    setResult(state, action) {
      if (state.number === '0' && action.payload === '0') {
        return state.number;
      }
      if (state.number.includes('.') && action.payload === '.') {
        return state.number;
      }
      state.number += action.payload;
    },
    setClear(state, action) {
      state.number = '';
    },
    setOperation(state, action) {
      state.result = state.number;
      state.number = null;
    },
    setEvaluet(state, action) {},
  },
});

export const { setResult, setClear, setOperation, setEvaluet } = calculateSlice.actions;

export default calculateSlice.reducer;
