import { useSelector, useDispatch } from 'react-redux';
import './styles.css';

function App() {
  const dispatch = useDispatch();
  const [{ currentOperand, previousOperand, operation }] = useSelector((state) => state.calculate);

  return (
    <div className="calculator-grid">
      <div className="output">
        <div className="previous-operand">2</div>
        <div className="current-operand">1</div>
      </div>
      <button className="span-two">AC</button>
      <button>DEL</button>
      <button>รท</button>
      <button>1</button>
      <button>2</button>
      <button>3</button>
      <button>*</button>
      <button>4</button>
      <button>5</button>
      <button>6</button>
      <button>+</button>
      <button>7</button>
      <button>8</button>
      <button>9</button>
      <button>-</button>
      <button>.</button>
      <button>0</button>
      <button className="span-two">=</button>
    </div>
  );
}

export default App;
