import { useSelector, useDispatch } from 'react-redux';
import React from 'react';
import './styles.css';

function App() {
  const [result, setResult] = React.useState('');

  function handleClick(event) {
    setResult(result.concat(event.target.name));
  }

  function handleClear() {
    setResult('');
  }

  function handleDelete() {
    setResult(result.slice(0, -1));
  }

  function handleCalculate() {
    try {
      setResult(eval(result).toString());
    } catch (error) {
      setResult('Error');
    }
  }
  return (
    <div className="calculator-grid">
      <div className="output">
        <div className="previous-operand">0</div>
        <div className="current-operand">{result}</div>
      </div>
      <button className="span-two" onClick={handClear}>
        AC
      </button>
      <button>DEL</button>
      <button>รท</button>
      <button digit="1">1</button>
      <button digit="2">2</button>
      <button digit="3">3</button>
      <button digit="*">*</button>
      <button digit="4">4</button>
      <button digit="5">5</button>
      <button digit="6">6</button>
      <button digit="+">+</button>
      <button digit="7">7</button>
      <button digit="8">8</button>
      <button digit="9">9</button>
      <button digit="-">-</button>
      <button digit=".">.</button>
      <button digit="0">0</button>
      <button className="span-two">=</button>
    </div>
  );
}

export default App;
