import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  currentOperand: '',
  previousOperand: '',
  operation: '',
};

export const calculateSlice = createSlice({
  name: 'calculate',
  initialState,
  reducers: {
    setCurrentOperand(state, action) {
      if (state.currentOperand === '0' && action.payload === '0') return state;
      if (action.payload === '.' && state.currentOperand.includes('.')) return state;
      state.currentOperand += action.payload;
    },
    setClear(state, action) {},
    setDelete(state, action) {},
    setEvaluet(state, action) {},
  },
});

export const { setCurrentOperand, setClear, setDelet, setEvaluet } = calculateSlice.actions;

export default calculateSlice.reducer;
